mapper.select:
select
count(*)
from charge_order
WHERE 1=1

    AND member_name like concat('%',#{name},'%')


    AND member_id = #{member_id}


    AND chargeruleid = #{chargerelueid}


    AND charge_state = #{state}


    AND  DATEDIFF(create_date,#{startTime}) >= 0


    AND  DATEDIFF(create_date,#{endTime})<0

select

from charge_order
WHERE 1=1

  AND member_name like concat
    ('%',#{name},'%')


    AND member_id = #{member_id}


    AND chargeruleid = #{chargerelueid}


    AND charge_state = #{state}


    AND  DATEDIFF(create_date,#{startTime}) >= 0


    AND  DATEDIFF(create_date,#{endTime})<0

order by id desc
limit #{begin},#{pageSize}

select * from charge_order WHERE 1=1 and charge_state = 1
AND member_id = #{member_id}
order by id desc limit #{begin},#{pageSize}

select a.id id, a.order_no orderNo, a.chargeruleid chargeruleid,a.charge_name chargeName, a.price price, a.member_id memberId, a.member_name memberName, a.charge_type chargeType, a.charge_state chargeState, a.coins_before coinsBefore, a.coins_after coinsAfter, a.coins_charge coinsCharge, a.coins_offer coinsOffer,a.create_date createDate, a.update_date updateDate, b.register_channel registerChannel, b.login_channel loginChannel, b.memberID memberNum,b.last_login_from lastLoginFrom from charge_order a LEFT JOIN t_member b on a.member_id = b.id WHERE 1=1
AND b.register_channel = #{registerChannel}
AND a.member_name like concat ('%',#{name},'%')
AND b.memberID = #{member_id}
AND a.chargeruleid = #{chargerelueid}
AND a.charge_state = #{state}
AND DATEDIFF(a.create_date,#{startTime}) >= 0
AND DATEDIFF(a.create_date,#{endTime})<0
AND b.last_login_from = #{lastLoginFrom}
AND b.last_login_from is NULL or b.last_login_from = 'iOS'
order by id desc limit #{begin},#{pageSize}

select
count(*)
from charge_order a LEFT JOIN t_member b on a.member_id = b.id
WHERE 1=1

    AND b.register_channel = #{registerChannel}


    AND a.member_name like concat('%',#{name},'%')


    AND b.memberID = #{member_id}


    AND a.chargeruleid = #{chargerelueid}


    AND a.charge_state = #{state}


    AND  DATEDIFF(a.create_date,#{startTime}) >= 0


    AND  DATEDIFF(a.create_date,#{endTime})<0



        AND b.last_login_from = #{lastLoginFrom}


        AND b.last_login_from is NULL
        or b.last_login_from = 'iOS'

select
count(DISTINCT b.id)
from charge_order a LEFT JOIN t_member b on a.member_id = b.id
WHERE 1=1

    AND b.register_channel = #{registerChannel}


    AND a.member_name like concat('%',#{name},'%')


    AND b.memberID = #{member_id}


    AND a.chargeruleid = #{chargerelueid}


    AND a.charge_state = #{state}


    AND  DATEDIFF(a.create_date,#{startTime}) >= 0


    AND  DATEDIFF(a.create_date,#{endTime})<0



        AND b.last_login_from = #{lastLoginFrom}


        AND b.last_login_from is NULL
        or b.last_login_from = 'iOS'

select
sum(a.price)
from charge_order a LEFT JOIN t_member b on a.member_id = b.id
WHERE 1=1

    AND b.register_channel = #{registerChannel}


    AND a.member_name like concat('%',#{name},'%')


    AND b.memberID = #{member_id}


    AND a.chargeruleid = #{chargerelueid}


    AND a.charge_state = #{state}


    AND  DATEDIFF(a.create_date,#{startTime}) >= 0


    AND  DATEDIFF(a.create_date,#{endTime})<0



        AND b.last_login_from = #{lastLoginFrom}


        AND b.last_login_from is NULL
        OR  b.last_login_from = 'iOS'
